def main(toplevel: object = None):
    """Main entry point for the application."""
    if toplevel is None:
        global root
        root = tk.Tk()
        root.protocol('WM_DELETE_WINDOW', root.destroy)
        # Creates a toplevel widget.
        _top1 = root
        global win
        win = pdrobot.Toplevel1(_top1)
        root.mainloop()
    else:
        # toplevel provided by test
        win = toplevel


